function deepClone(obj = {}){
  if(typeof obj !== 'object'||obj === null){
    return obj;
  }
  //初始化返回结果
  let result;
  if(obj.instanceof Array){
    result = []
  }else{
    result = {}
  }
  for(let key in obj){
    if(obj.hasOwnProperty(key)){    //保证是这个对象自己拥有的私有属性，不是原型的属性
        //调用递归
       result[key] = deepClone(obj[key])
    }
  }
  return result;
}

